    b[n].type = sqlite::bind::integer;
    b[n].buffer = &i.v_int_value;
    b[n].is_null = &i.v_int_null;
    n++;

    // v_double
    //
    b[n].type = sqlite::bind::real;
    b[n].buffer = &i.v_double_value;
    b[n].is_null = &i.v_double_null;
    n++;

    // id_
